mapper.insert:
INSERT INTO TBL_MEMBER
(
  MEMBER_ID
, MEMBER_PWD
, MEMBER_NAME
, MEMBER_CONTACT
, MEMBER_NUM
, MEMBER_BIRTHDAY
, MEMBER_AGE
, MEMBER_GENDER
, MEMBER_ADDRESS
, MEMBER_STATUS
)
VALUES
(
  #{ id }
, #{ pwd }
, #{ name }
, #{ contact }
, #{ num }
, #{ birthday }
, #{ age }
, #{ gender }
, #{ address }
, DEFAULT
)

INSERT INTO TBL_MEMBER_ROLE
(
  MEMBER_ID
, AUTHORITY_CODE
)
VALUES
(
  #{ memberId }
, #{ authorityCode }
)

mapper.select:
SELECT COUNT(*)
  FROM TBL_MEMBER
 WHERE MEMBER_CONTACT = #{ contact }

SELECT COUNT(*)
  FROM TBL_MEMBER
 WHERE MEMBER_ID = #{ id }
   AND MEMBER_CONTACT = #{ contact }

SELECT MEMBER_ID
  FROM TBL_MEMBER
 WHERE MEMBER_NAME = #{ name }
   AND MEMBER_CONTACT = #{ contact }
   AND MEMBER_STATUS = 'Y'

mapper.update:
UPDATE TBL_MEMBER
   SET MEMBER_PWD = #{ pwd }
 WHERE MEMBER_ID = #{ id }
   AND MEMBER_STATUS = 'Y'

UPDATE TBL_MEMBER
   SET MEMBER_NAME = #{ name }
     , MEMBER_CONTACT = #{ contact }
     , MEMBER_ADDRESS = #{ address }
     , MEMBER_BIRTHDAY = #{ birthday }
     , MEMBER_GENDER = #{ gender }
     , MEMBER_NUM = #{ num }
 WHERE MEMBER_ID = #{ id }
   AND MEMBER_STATUS = 'Y'

mapper.select:
SELECT A.RNUM , A.MEMBER_ID , A.MEMBER_NAME , A.MEMBER_CONTACT , A.MEMBER_NUM , A.MEMBER_BIRTHDAY , A.MEMBER_AGE , A.MEMBER_GENDER , A.MEMBER_ADDRESS , A.MEMBER_STATUS , D.MEMBER_ID REF_MEMBER_ID , D.AUTHORITY_CODE REF_AUTHORITY_CODE , E.AUTHORITY_CODE REF_AUTHORITY_CODE2 , E.AUTHORITY_NAME , E.AUTHORITY_DESC FROM (SELECT ROWNUM RNUM , B.MEMBER_ID , B.MEMBER_NAME , B.MEMBER_CONTACT , B.MEMBER_NUM , B.MEMBER_BIRTHDAY , B.MEMBER_AGE , B.MEMBER_GENDER , B.MEMBER_ADDRESS , B.MEMBER_STATUS FROM (SELECT C.MEMBER_ID , C.MEMBER_NAME , C.MEMBER_CONTACT , C.MEMBER_NUM , C.MEMBER_BIRTHDAY , C.MEMBER_AGE , C.MEMBER_GENDER , C.MEMBER_ADDRESS , C.MEMBER_STATUS FROM TBL_MEMBER C
C.MEMBER_NAME LIKE '%' || #{searchKeyword} || '%'
C.MEMBER_ID LIKE '%' || #{searchKeyword} || '%'
C.MEMBER_NUM LIKE '%' || #{searchKeyword} || '%'
C.MEMBER_CONTACT LIKE '%' || #{searchKeyword} || '%'
) B
WHERE ROWNUM <= #{ paginationInfo.firstRecordIndex } + #{ recordsPerPage }
) A LEFT JOIN TBL_MEMBER_ROLE D ON (A.MEMBER_ID = D.MEMBER_ID) LEFT JOIN TBL_AUTHORITY E ON (D.AUTHORITY_CODE = E.AUTHORITY_CODE) WHERE A.RNUM > #{ paginationInfo.firstRecordIndex } AND A.MEMBER_STATUS = 'Y' AND E.AUTHORITY_CODE != 3 ORDER BY A.RNUM

mapper.update:
UPDATE TBL_MEMBER_ROLE
   SET AUTHORITY_CODE = 2
 WHERE MEMBER_ID = #{ id }
