mapper.insert:
INSERT INTO TBL_MEMBER
(
  MEMBER_ID
, MEMBER_PWD
, MEMBER_NAME
, MEMBER_CONTACT
, MEMBER_NUM
, MEMBER_BIRTHDAY
, MEMBER_AGE
, MEMBER_GENDER
, MEMBER_ADDRESS
, MEMBER_TYPE
, MEMBER_STATUS
)
VALUES
(
  #{ id }
, #{ pwd }
, #{ name }
, #{ contact }
, #{ num }
, #{ birthday }
, #{ age }
, #{ gender }
, #{ address }
, DEFAULT
, DEFAULT
)

INSERT INTO TBL_MEMBER_ROLE
(
  MEMBER_ID
, AUTHORITY_CODE
)
VALUES
(
  #{ memberId }
, #{ authorityCode }
)

mapper.select:
SELECT COUNT(*)
  FROM TBL_MEMBER
 WHERE MEMBER_CONTACT = #{ contact }
