mapper.select:
SELECT E.RNUM , E.TRN_NUM , E.TRN_HOLD , E.MEMBER_NAME , E.MEMBER_GENDER , E.MEMBER_ID , E.MEMBER_CONTACT , E.TRN_CAREER FROM (SELECT ROWNUM RNUM , D.TRN_NUM , D.TRN_HOLD , D.MEMBER_NAME , D.MEMBER_GENDER , D.MEMBER_ID , D.MEMBER_CONTACT , D.TRN_CAREER FROM (SELECT B.TRN_NUM , B.TRN_HOLD , A.MEMBER_NAME , A.MEMBER_GENDER , A.MEMBER_ID , A.MEMBER_CONTACT , B.TRN_CAREER FROM TBL_MEMBER A JOIN TBL_TRN_INFO B ON(A.MEMBER_ID = B.TRN_INFO_MEMBER_ID)
(B.TRN_HOLD = '재직' OR B.TRN_HOLD = '퇴사')
B.TRN_HOLD = '재직'
B.TRN_HOLD = '퇴사'
AND A.MEMBER_NAME LIKE '%' || #{searchKeyword} || '%'
AND A.MEMBER_ID LIKE '%' || #{searchKeyword} || '%'
AND A.MEMBER_CONTACT LIKE '%' || #{searchKeyword} || '%'
) D
WHERE ROWNUM <= #{ paginationInfo.firstRecordIndex } + #{ recordsPerPage }
) E JOIN TBL_MEMBER_ROLE C ON(E.MEMBER_ID = C.MEMBER_ID) WHERE C.AUTHORITY_CODE = 2 AND E.RNUM > #{ paginationInfo.firstRecordIndex } ORDER BY E.RNUM
